Table.Script:
SELECT
	@Document.SMOID AS [rf_SMOID],
	MedRecordID,
	N_REC,
	MKABID
FROM
	@MedRecord,
	@MKAB
WHERE PersonGUID = UGUID

SELECT
	@Document.SMOID AS [SMOID],
	'@VERS' AS [VERS],
	'@FILENAME' AS [FILENAME],
	'@FCOD' AS [MCODE],
	COUNT(*) AS [NRECORDS]
FROM
	@MedRecord,
	@MKAB
WHERE PersonGUID = UGUID

Param.ParamDefaultValue:
SELECT LTRIM(RTRIM(MCOD))
FROM oms_LPU
WHERE LPUID = @LPUID;

SELECT FCOD
FROM oms_LPU
WHERE LPUID = @LPUID;

SELECT Lic
FROM oms_LPU
WHERE LPUID = @LPUID;

SELECT ISNULL(MAX(CommonReestrTypeID), 0)
FROM hlt_CommonReestrType
WHERE COD = 'R_ARM_01_1';

SELECT MAX(BlankTemplateID)
FROM hlt_BlankTemplate
WHERE GUID = 'BE80AFC5-0FD8-E811-9C06-2C4D546667A5';

ScriptsAfterLoad.Script:
IF NOT OBJECT_ID('@MKAB', 'U') IS NULL
	DROP TABLE @MKAB;

IF NOT OBJECT_ID('@MedRecord', 'U') IS NULL
	DROP TABLE @MedRecord;
